--
-- 表的结构 `juniorlog`
--

CREATE TABLE IF NOT EXISTS `juniorlog` (
  `LogId` int(11) NOT NULL AUTO_INCREMENT COMMENT '日志ID',
  `LogHead` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '日志标题',
  `LogType` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '日志类型',
  `LogAuthor` varchar(20) CHARACTER SET utf8 NOT NULL COMMENT '日志作者',
  `LogContent` text CHARACTER SET utf8 NOT NULL COMMENT '日志内容',
  `LogPic` mediumblob NOT NULL COMMENT '日志图片',
  `ReadTimes` int(11) NOT NULL COMMENT '阅读次数',
  `UpTimes` date NOT NULL DEFAULT '0000-00-00' COMMENT '发布时间',
  PRIMARY KEY (`LogId`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='大三日志表' AUTO_INCREMENT=7 ;

--
-- 转存表中的数据 `juniorlog`
--

INSERT INTO `juniorlog` (`LogId`, `LogHead`, `LogType`, `LogAuthor`, `LogContent`, `LogPic`, `ReadTimes`, `UpTimes`) VALUES
(2, '邓肯称未来是你们的', '体育新闻', '呆呆', '时光不可逆，人老不能怨。在任何一个职业当中，总是在发生着年轻和老去的故事，因此传承成为一种必不可少的步骤，NBA也不例外。当老球星跟年轻人说未来是属于你的那证明他把你看成了联盟未来的希望，这就是一种传承。', 0x31, 30, '2015-12-07'),
(3, '勇士豪取22连胜', '体育新闻', '萌神', '【今日谈：快乐篮球，不止于进攻】进攻行云流水，每个人都乐意分享球，场均助攻近30次，主力经常提早打卡下班，替补得以更多地展示自己。我相信在这样的球队里打球，没有人会觉得不快乐，但更令人心生敬畏的是，他们同时享受着防守带来的乐趣。官网带你了解强大勇士背后的秘诀：', 0x32, 6, '2015-09-09'),
(4, '1', '大一日志', '李四', '2', '', 0, '2015-12-08'),
(5, '【信管】各位现在都在哪个城市学习生活呢？', 'freshlog', '14李四', '<p><span style="font-size: 14px; line-height: 17.08px; white-space: normal;">2015-12-08</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 17.08px; white-space: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; background: transparent;">相同的专业相同城市的信管学子可以一起努力吖！</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 17.08px; white-space: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; background: transparent;"><a class="at j_user_card " data-field="{&#39;un&#39;:&#39;回忆国傀儡&#39;}" alog-group="p_author" target="_blank" href="http://tieba.baidu.com/home/main?un=%E5%9B%9E%E5%BF%86%E5%9B%BD%E5%82%80%E5%84%A1&ie=utf-8&fr=pb" username="回忆国傀儡" style="box-sizing: content-box; margin: 0px; padding: 0px; font-size: 12px; vertical-align: baseline; color: rgb(45, 100, 179); text-decoration: none; outline: 0px; -webkit-tap-highlight-color: rgb(255, 102, 0); transition: color 0.4s ease-in-out; position: relative; zoom: 1; line-height: 22px; background: rgb(247, 248, 250);">回忆国傀儡</a><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; line-height: 22px; background: rgb(247, 248, 250);">:&nbsp;</span><span class="lzl_content_main" style="box-sizing: content-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 20px; background: rgb(247, 248, 250);">回复 施吾爱先森 :现在在长清，以前在历下区的校区上，刚刚搬到长清</span><img src="http://img.baidu.com/hi/jx2/j_0002.gif" _src="http://img.baidu.com/hi/jx2/j_0002.gif" style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;"/></span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 17.08px; white-space: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; background: transparent;"><img src="http://127.0.0.1/tmlog/html/report-article/php/upload/20151208/14495861616322.jpg" _src="http://127.0.0.1/tmlog/html/report-article/php/upload/20151208/14495861616322.jpg" style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;"/></span></p><p><br/></p><p><br/></p>', '', 0, '2015-12-08'),
(6, '推荐系统中的常用算法', 'JuniorLog', 'admin', '<p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">在推荐系统简介中，我们给出了推荐系统的一般框架。很明显，推荐方法是整个推荐系统中最核心、最关键的部分，很大程度上决定了推荐系统性能的优劣。目前，主要的推荐方法包括：基于内容推荐、协同过滤推荐、基于关联规则推荐、基于效用推荐、基于知识推荐和组合推荐。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">一、基于内容推荐</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">基 于内容的推荐（Content-based Recommendation）是信息过滤技术的延续与发展，它是建立在项目的内容信息上作出推荐的，而不需要依据用户对项目的评价意见，更多地需要用机 器学习的方法从关于内容的特征描述的事例中得到用户的兴趣资料。在基于内容的推荐系统中，项目或对象是通过相关的特征的属性来定义，系统基于用户评价对象 的特征，学习用户的兴趣，考察用户资料与待预测项目的相匹配程度。用户的资料模型取决于所用学习方法，常用的有决策树、神经网络和基于向量的表示方法等。 基于内容的用户资料是需要有用户的历史数据，用户资料模型可能随着用户的偏好改变而发生变化。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">基于内容推荐方法的优点是：<br/>&nbsp;1）不需要其它用户的数据，没有冷开始问题和稀疏问题。<br/>&nbsp;2）能为具有特殊兴趣爱好的用户进行推荐。<br/>&nbsp;3）能推荐新的或不是很流行的项目，没有新项目问题。<br/>&nbsp;4）通过列出推荐项目的内容特征，可以解释为什么推荐那些项目。<br/>&nbsp;5）已有比较好的技术，如关于分类学习方面的技术已相当成熟。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">缺点是要求内容能容易抽取成有意义的特征，要求特征内容有良好的结构性，并且用户的口味必须能够用内容特征形式来表达，不能显式地得到其它用户的判断情况。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">二、协同过滤推荐</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">协 同过滤推荐（Collaborative Filtering Recommendation）技术是推荐系统中应用最早和最为成功的技术之一。它一般采用最近邻技术，利用用户的历史喜好信息计算用户之间的距离，然后 利用目标用户的最近邻居用户对商品评价的加权评价值来预测目标用户对特定商品的喜好程度，系统从而根据这一喜好程度来对目标用户进行推荐。协同过滤最大优 点是对推荐对象没有特殊的要求，能处理非结构化的复杂对象，如音乐、电影。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">协 同过滤是基于这样的假设：为一用户找到他真正感兴趣的内容的好方法是首先找到与此用户有相似兴趣的其他用户，然后将他们感兴趣的内容推荐给此用户。其基本 思想非常易于理解，在日常生活中，我们往往会利用好朋友的推荐来进行一些选择。协同过滤正是把这一思想运用到电子商务推荐系统中来，基于其他用户对某一内 容的评价来向目标用户进行推荐。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">基于协同过滤的推荐系统可以说是从用户的角度来进行相应推荐的，而且是自动的，即用户获得的推荐是系统从购买模式或浏览行为等隐式获得的，不需要用户努力地找到适合自己兴趣的推荐信息，如填写一些调查表格等。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">和基于内容的过滤方法相比，协同过滤具有如下的优点：</p><ul style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; list-style-position: outside; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;"><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">1）&nbsp;能够过滤难以进行机器自动内容分析的信息，如艺术品，音乐等。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">2）&nbsp;共享其他人的经验，避免了内容分析的不完全和不精确，并且能够基于一些复杂的，难以表述的概念（如信息质量、个人品味）进行过滤。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">3）&nbsp;有推荐新信息的能力。可以发现内容上完全不相似的信息，用户对推荐信息的内容事先是预料不到的。这也是协同过滤和基于内容的过滤一个较大的差别，基于内容的过滤推荐很多都是用户本来就熟悉的内容，而协同过滤可以发现用户潜在的但自己尚未发现的兴趣偏好。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">4）&nbsp;能够有效的使用其他相似用户的反馈信息，较少用户的反馈量，加快个性化学习的速度。</li></ul><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">虽然协同过滤作为一种典型的推荐技术有其相当的应用，但协同过滤仍有许多的问题需要解决。最典型的问题有稀疏问题（Sparsity）和可扩展问题（Scalability）。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">三、基于关联规则推荐</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">基 于关联规则的推荐（Association Rule-based Recommendation）是以关联规则为基础，把已购商品作为规则头，规则体为推荐对象。关联规则挖掘可以发现不同商品在销售过程中的相关性，在零 售业中已经得到了成功的应用。管理规则就是在一个交易数据库中统计购买了商品集X的交易中有多大比例的交易同时购买了商品集Y，其直观的意义就是用户在购 买某些商品的时候有多大倾向去购买另外一些商品。比如购买牛奶的同时很多人会同时购买面包。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">算法的第一步关联规则的发现最为关键且最耗时，是算法的瓶颈，但可以离线进行。其次，商品名称的同义性问题也是关联规则的一个难点。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">四、基于效用推荐</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">基 于效用的推荐（Utility-based Recommendation）是建立在对用户使用项目的效用情况上计算的，其核心问题是怎么样为每一个用户去创建一个效用函数，因此，用户资料模型很大 程度上是由系统所采用的效用函数决定的。基于效用推荐的好处是它能把非产品的属性，如提供商的可靠性（Vendor Reliability）和产品的可得性（Product Availability）等考虑到效用计算中。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">五、基于知识推荐</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">基 于知识的推荐（Knowledge-based Recommendation）在某种程度是可以看成是一种推理（Inference）技术，它不是建立在用户需要和偏好基础上推荐的。基于知识的方法因 它们所用的功能知识不同而有明显区别。效用知识（Functional Knowledge）是一种关于一个项目如何满足某一特定用户的知识，因此能解释需要和推荐的关系，所以用户资料可以是任何能支持推理的知识结构，它可以 是用户已经规范化的查询，也可以是一个更详细的用户需要的表示。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">六、组合推荐</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">由 于各种推荐方法都有优缺点，所以在实际中，组合推荐（Hybrid Recommendation）经常被采用。研究和应用最多的是内容推荐和协同过滤推荐的组合。最简单的做法就是分别用基于内容的方法和协同过滤推荐方法 去产生一个推荐预测结果，然后用某方法组合其结果。尽管从理论上有很多种推荐组合方法，但在某一具体问题中并不见得都有效，组合推荐一个最重要原则就是通 过组合后要能避免或弥补各自推荐技术的弱点。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">在组合方式上，有研究人员提出了七种组合思路：</p><ul style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; list-style-position: outside; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;"><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">1）加权（Weight）：加权多种推荐技术结果。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">2）变换（Switch）：根据问题背景和实际情况或要求决定变换采用不同的推荐技术。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">3）混合（Mixed）：同时采用多种推荐技术给出多种推荐结果为用户提供参考。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">4）特征组合（Feature combination）：组合来自不同推荐数据源的特征被另一种推荐算法所采用。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">5）层叠（Cascade）：先用一种推荐技术产生一种粗糙的推荐结果，第二种推荐技术在此推荐结果的基础上进一步作出更精确的推荐。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">6）特征扩充（Feature augmentation）：一种技术产生附加的特征信息嵌入到另一种推荐技术的特征输入中。</li><li style="border: 0px; margin: 0px 0px 5px 30px; padding: 0px;">7）元级别（Meta-level）：用一种推荐方法产生的模型作为另一种推荐方法的输入。</li></ul><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">七、主要推荐方法的对比</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">各种推荐方法都有其各自的优点和缺点，见表1。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;">&nbsp;</p><table border="1" cellspacing="0" cellpadding="0" style="border: 1px solid rgb(232, 232, 232); margin-top: 0px; margin-bottom: 20px; padding: 0px 5px; font-size: 15px; border-spacing: 0px; width: 599px; color: rgb(46, 46, 46); font-family: &#39;Microsoft YaHei&#39;, 宋体, &#39;Myriad Pro&#39;, Lato, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; white-space: normal;"><tbody style="border: 0px; margin: 0px; padding: 0px;"><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="541" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">表1 主要推荐方法对比</div></td></tr><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="120" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">推荐方法</div></td><td width="245" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">优点</div></td><td width="176" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">缺点</div></td></tr><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="120" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">基于内容推荐</div></td><td width="245" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">推荐结果直观，容易解释；</div><div style="border: 0px; margin: 0px; padding: 0px;">不需要领域知识</div></td><td width="176" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">稀疏问题；新用户问题；</div><div style="border: 0px; margin: 0px; padding: 0px;">复杂属性不好处理；</div><div style="border: 0px; margin: 0px; padding: 0px;">要有足够数据构造分类器</div></td></tr><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="120" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">协同过滤推荐</div></td><td width="245" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">新异兴趣发现、不需要领域知识；</div><div style="border: 0px; margin: 0px; padding: 0px;">随着时间推移性能提高；</div><div style="border: 0px; margin: 0px; padding: 0px;">推荐个性化、自动化程度高；</div><div style="border: 0px; margin: 0px; padding: 0px;">能处理复杂的非结构化对象</div></td><td width="176" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">稀疏问题；</div><div style="border: 0px; margin: 0px; padding: 0px;">可扩展性问题；</div><div style="border: 0px; margin: 0px; padding: 0px;">新用户问题；</div><div style="border: 0px; margin: 0px; padding: 0px;">质量取决于历史数据集；</div><div style="border: 0px; margin: 0px; padding: 0px;">系统开始时推荐质量差；</div></td></tr><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="120" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">基于规则推荐</div></td><td width="245" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">能发现新兴趣点；</div><div style="border: 0px; margin: 0px; padding: 0px;">不要领域知识</div></td><td width="176" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">规则抽取难、耗时；</div><div style="border: 0px; margin: 0px; padding: 0px;">产品名同义性问题；</div><div style="border: 0px; margin: 0px; padding: 0px;">个性化程度低；</div></td></tr><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="120" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">基于效用推荐</div></td><td width="245" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">无冷开始和稀疏问题；</div><div style="border: 0px; margin: 0px; padding: 0px;">对用户偏好变化敏感；</div><div style="border: 0px; margin: 0px; padding: 0px;">能考虑非产品特性</div></td><td width="176" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">用户必须输入效用函数；</div><div style="border: 0px; margin: 0px; padding: 0px;">推荐是静态的，灵活性差；</div><div style="border: 0px; margin: 0px; padding: 0px;">属性重叠问题；</div></td></tr><tr style="border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(232, 232, 232); margin: 0px; padding: 0px;"><td width="120" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">基于知识推荐</div></td><td width="245" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">能把用户需求映射到产品上；</div><div style="border: 0px; margin: 0px; padding: 0px;">能考虑非产品属性</div></td><td width="176" style="border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-right-color: rgb(232, 232, 232); margin: 0px; padding-right: 5px; padding-left: 5px; vertical-align: middle; text-align: center;"><div style="border: 0px; margin: 0px; padding: 0px;">知识难获得；</div><div style="border: 0px; margin: 0px; padding: 0px;">推荐是静态的</div></td></tr></tbody></table><p><br/></p>', '', 0, '2015-12-10');

