--
-- 表的结构 `sophomorelog`
--

CREATE TABLE IF NOT EXISTS `sophomorelog` (
  `LogId` int(11) NOT NULL AUTO_INCREMENT COMMENT '日志ID',
  `LogHead` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '日志标题',
  `LogType` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '日志类型',
  `LogAuthor` varchar(20) CHARACTER SET utf8 NOT NULL COMMENT '日志作者',
  `LogContent` text CHARACTER SET utf8 NOT NULL COMMENT '日志内容',
  `LogPic` mediumblob NOT NULL COMMENT '日志图片',
  `ReadTimes` int(11) NOT NULL COMMENT '阅读次数',
  `UpTimes` date NOT NULL DEFAULT '0000-00-00' COMMENT '发布时间',
  PRIMARY KEY (`LogId`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='大二日志表' AUTO_INCREMENT=10 ;

--
-- 转存表中的数据 `sophomorelog`
--

INSERT INTO `sophomorelog` (`LogId`, `LogHead`, `LogType`, `LogAuthor`, `LogContent`, `LogPic`, `ReadTimes`, `UpTimes`) VALUES
(2, '邓肯称未来是你们的', '体育新闻', '呆呆', '时光不可逆，人老不能怨。在任何一个职业当中，总是在发生着年轻和老去的故事，因此传承成为一种必不可少的步骤，NBA也不例外。当老球星跟年轻人说未来是属于你的那证明他把你看成了联盟未来的希望，这就是一种传承。', 0x31, 30, '2015-12-07'),
(3, '勇士豪取22连胜', '体育新闻', '萌神', '【今日谈：快乐篮球，不止于进攻】进攻行云流水，每个人都乐意分享球，场均助攻近30次，主力经常提早打卡下班，替补得以更多地展示自己。我相信在这样的球队里打球，没有人会觉得不快乐，但更令人心生敬畏的是，他们同时享受着防守带来的乐趣。官网带你了解强大勇士背后的秘诀：', 0x32, 6, '2015-09-09'),
(4, '1', '大一日志', '李四', '2', '', 0, '2015-12-08'),
(5, '【信管】各位现在都在哪个城市学习生活呢？', 'freshlog', '14李四', '<p><span style="font-size: 14px; line-height: 17.08px; white-space: normal;">2015-12-08</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 17.08px; white-space: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; background: transparent;">相同的专业相同城市的信管学子可以一起努力吖！</span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 17.08px; white-space: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; background: transparent;"><a class="at j_user_card " data-field="{&#39;un&#39;:&#39;回忆国傀儡&#39;}" alog-group="p_author" target="_blank" href="http://tieba.baidu.com/home/main?un=%E5%9B%9E%E5%BF%86%E5%9B%BD%E5%82%80%E5%84%A1&ie=utf-8&fr=pb" username="回忆国傀儡" style="box-sizing: content-box; margin: 0px; padding: 0px; font-size: 12px; vertical-align: baseline; color: rgb(45, 100, 179); text-decoration: none; outline: 0px; -webkit-tap-highlight-color: rgb(255, 102, 0); transition: color 0.4s ease-in-out; position: relative; zoom: 1; line-height: 22px; background: rgb(247, 248, 250);">回忆国傀儡</a><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; line-height: 22px; background: rgb(247, 248, 250);">:&nbsp;</span><span class="lzl_content_main" style="box-sizing: content-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 20px; background: rgb(247, 248, 250);">回复 施吾爱先森 :现在在长清，以前在历下区的校区上，刚刚搬到长清</span><img src="http://img.baidu.com/hi/jx2/j_0002.gif" _src="http://img.baidu.com/hi/jx2/j_0002.gif" style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;"/></span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; line-height: 17.08px; white-space: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><span style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; letter-spacing: normal; line-height: 24px; background: transparent;"><img src="http://127.0.0.1/tmlog/html/report-article/php/upload/20151208/14495861616322.jpg" _src="http://127.0.0.1/tmlog/html/report-article/php/upload/20151208/14495861616322.jpg" style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;"/></span></p><p><br/></p><p><br/></p>', '', 0, '2015-12-08'),
(6, '我是帅气的标题', 'SophomoreL', '13李四', '<p>你怎么知道哦<img src="http://127.0.0.1/xg/html/report-article/php/upload/20151209/14496683918429.jpg" _src="http://127.0.0.1/xg/html/report-article/php/upload/20151209/14496683918429.jpg"/></p>', '', 0, '2015-12-09'),
(7, '程序员有哪些电脑科技的技能可以让非程序员感到神奇？', 'SophomoreL', 'admin', '<p><span style="white-space: nowrap;">来源：知乎</span></p><p><span style="white-space: nowrap;">大学的时候，系里的机子都是win 2k。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">晚上赶项目，我和一个哥们在楼下的平房里折腾数据，到了九点多，没什么进展，就出来看看，对面系楼一片漆黑，就一间屋亮着。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">哥们说，他室友“朱XX”在那个屋，可能在看黄网呢。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">咱们郁闷，他却爽，这不好，本着维护正义，净化网络环境的宗旨，我们决定挺身而出。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">那时的 windows 有个 net send 命令，可以在对方机器显示个对话框，类似这样，</span></p><p><br/></p><p><span style="white-space: nowrap;">我们把自己的电脑名称改为 “网络中心”，给三楼的机器发了个消息，</span></p><p><span style="white-space: nowrap;"><img src="https://pic3.zhimg.com/16cc3e6198da31ee07075e09108e25d6_b.jpg" _src="https://pic3.zhimg.com/16cc3e6198da31ee07075e09108e25d6_b.jpg"/></span></p><p><span style="white-space: nowrap;">从 “网络中心” 到 “XXXX” 的消息：</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">“朱XX同学，我们检测到你正在浏览不健康网站，已记录在案，将通知系办处理。”</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">很快，楼上的灯就熄了，一个人影从楼里匆匆出来，消失在夜色里。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">回到寝室，朱同学正坐在床沿上吸烟，一脸凝重，好像有心事的样子。良久，说了一句，</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">“他们是怎么知道我名字的？”</span></p><p><br/></p>', '', 0, '2015-12-10'),
(8, '程序员有哪些电脑科技的技能可以让非程序员感到神奇【连更1】', 'SophomoreL', 'admin', '<p><span style="white-space: nowrap;">来源：知乎</span></p><p><span style="white-space: nowrap;">很多年前吧，给企业用户做windows server的技术支持，是24x7的，客户的服务器如果出问题比如当机了是可以随时打电话给我们的，我们称之为severity A，代表最高级别响应。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">那天我轮班，但是又有人找我踢球，我就开开心心地去踢了。踢到一半电话响了，我当时就想完了这球踢不成了。有个客户说他重启了下服务器现在每次启动就蓝屏进不了系统了。服务器恢复不了他就要跪了。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">后来我就对小伙伴说我先去守一会儿门，一边守门一边接客户电话。了解了下蓝屏代码是0x7b，恩这个算容易的，这个直接原因是系统进入kernel后读不出启动盘，一般就是硬盘的驱动无法正确加载。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">然后就叫客户把系统注册表文件copy到另一台机器上用regedit打开，再把和硬盘设备驱动相关的条目一条条读给我听，我默记在心里然后人脑运行找碴程序，最后发现有一个filter driver的配置项不完整，在某处申明了要挂在硬盘设备上但自己的device没有配。这种一般是这个驱动的安装程序有bug造成的。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">找到问题后指导客户把不完整的驱动设备信息删掉。因为这个驱动是某磁盘备份软件加装的，所以去掉也没什么关系。然后把修改后的注册表文件放回服务器结果就好了。</span></p><p><span style="white-space: nowrap;"><br/></span></p><p><span style="white-space: nowrap;">客户表示万分感谢，可是我一直没敢告诉他我还同时在守门啊，并且没丢球啊。后来还有感谢信呢。</span></p><p><br/></p>', '', 0, '2015-12-10'),
(9, '程序员有哪些电脑科技的技能可以让非程序员感到神奇【连更2】', 'SophomoreL', 'admin', '<div>来源：知乎<br/><br/><p>2015.09.09更新：</p><p>之前只放了原文链接，忘记声明转载了，现在补上。</p><p>文章转载自博客园，作者是<a href="http://home.cnblogs.com/u/index-html/" class=" wrap external" target="_blank" rel="nofollow noreferrer">EtherDream<em class="icon-external"></em></a>，他的博客里还有很多其他有意思的文章！</p><p>谢谢大家的赞，这是我前(搬)进(砖)的动力！</p><p>===================================================</p><br/><p><strong>一根网线发起的攻击</strong></p><p>原文链接：<a href="http://www.cnblogs.com/index-html/p/cable_attack.html" class=" wrap external" target="_blank" rel="nofollow noreferrer">【趣事】一根网线发起的攻击<em class="icon-external"></em></a></p><br/><p>刚上大学没多久，就遇到件头疼事。</p><p>富二代们刚来就带着笔记本电脑，这让咱们只能玩手机的屌丝辈们羡慕嫉妒恨。要命的事来了，晚上断电不断网，于是熄灯后笔记本仍然可以玩。</p><p>不巧的是，我们寝室也有个。常常熄灯后，非得把电池用干净才罢休。边游戏边语音，还放着音乐，备受煎熬。虽经劝说有所好改，但过不了几天又会复原。</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic4.zhimg.com/e9aa9c7281c80234842666f4ec25965b_b.png&amp;quot; data-rawwidth=&amp;quot;474&amp;quot; data-rawheight=&amp;quot;414&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;474&amp;quot; data-original=&amp;quot;https://pic4.zhimg.com/e9aa9c7281c80234842666f4ec25965b_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic4.zhimg.com/e9aa9c7281c80234842666f4ec25965b_b.png" data-rawwidth="474" data-rawheight="414" class="origin_image zh-lightbox-thumb lazy" width="474" data-original="https://pic4.zhimg.com/e9aa9c7281c80234842666f4ec25965b_r.png" data-actualsrc="https://pic4.zhimg.com/e9aa9c7281c80234842666f4ec25965b_b.png" style="width: 474px; height: 414px; display: block;"/><br/><p>为了迫切改变这个状况，但又不想和新认识的同学扯，于是决定用技术方案解决。</p><p>可当时的家当只有一部诺基亚滑盖手机，没有装备一切都是空谈。唯一可行的，只有偷偷在他电脑里设置个计划任务，晚上自动关机。但那样万一发现了更不好，根本没有技术含量。</p><p>无奈，只能把目光转移到电脑之外，网络上。如果不能上网，就算电脑能用，也不至于熬夜玩单机游戏吧。</p><p>既然刚来时他的网线插口就能用，想必我这儿的也有信号。上一届的肯定都开通过，总不至于走了以后还封掉。趁着有天寝室没人，我把那笔记本的网线拖到我这边一试，果然有信号！顿时来了精神，感觉有希望了！</p><p>也许你会说，总不可能把网线连到诺基亚上，然后用什么恶搞软件吧~ 当然不可能，那时的手机哪有这么先进。</p><p>事实上，我们不用任何软！件！，甚至可以不用硬！件！ —— 除了一根网线之外。</p><p>要说如何玩转网线，还能从之前安装机顶盒的那天说起。</p><p>曾有段时间，很多城市开始流行起数字电视。我们这也不例外，挨家挨户的赠送机顶盒，还免费上门安装。华数电视本来就和网通是一家，数字电视当然就是共享网通的宽带了。</p><p>由于之前已开通了网通，这次又要给机顶盒连网，我想至少得送个交换机才行吧。然而，安装的师傅一进来，既没掏出交换机、甚至连集线器也没有，反而一剪刀把网线给割了！</p><p>当时就惊呆了，这究竟是搞哪门子鬼。尽管那时对网络链路协议玩的挺嗨的，但物理层上的却是一窍不通。那师傅不慌不忙的说，网线只要四个就够了，还有些就是备用的。于是从之前的线里，拆了四根给机顶盒。</p><p>这大出之前所料，居然没用任何设备就把机顶盒接上了！于是，又开始异想天开了。。。</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic1.zhimg.com/b348aa000278c2c9add4327c27fb508c_b.png&amp;quot; data-rawwidth=&amp;quot;787&amp;quot; data-rawheight=&amp;quot;402&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;787&amp;quot; data-original=&amp;quot;https://pic1.zhimg.com/b348aa000278c2c9add4327c27fb508c_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic1.zhimg.com/b348aa000278c2c9add4327c27fb508c_b.png" data-rawwidth="787" data-rawheight="402" class="origin_image zh-lightbox-thumb lazy" width="787" data-original="https://pic1.zhimg.com/b348aa000278c2c9add4327c27fb508c_r.png" data-actualsrc="https://pic1.zhimg.com/b348aa000278c2c9add4327c27fb508c_b.png" style="width: 584px; height: 298.307px; display: block;"/><br/><p>这分出来的两股，在交换机来看是不是两个独立用户？如果把他们接在一起，效果和一线插两口相同吗？能一样短路局域网吗？</p><p>怀着兴奋的心情一测，果然可以！真把整个小区的网络搞挂了！</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic3.zhimg.com/ed50d6ab9a38ae916f0500f4bd44f94e_b.png&amp;quot; data-rawwidth=&amp;quot;493&amp;quot; data-rawheight=&amp;quot;263&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;493&amp;quot; data-original=&amp;quot;https://pic3.zhimg.com/ed50d6ab9a38ae916f0500f4bd44f94e_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic3.zhimg.com/ed50d6ab9a38ae916f0500f4bd44f94e_b.png" data-rawwidth="493" data-rawheight="263" class="origin_image zh-lightbox-thumb lazy" width="493" data-original="https://pic3.zhimg.com/ed50d6ab9a38ae916f0500f4bd44f94e_r.png" data-actualsrc="https://pic3.zhimg.com/ed50d6ab9a38ae916f0500f4bd44f94e_b.png" style="width: 493px; height: 263px; display: block;"/><br/><p>在恢复之后很长段时间里，一直拨不上号。在嗅探器里发现好多邻居们的也在不断的拨号。显然，刚刚那接通两个 4 股线，把外部的 STP 包也转发了，导致小区网络被外部隔离了。</p><p>这一天，改变了之前的看法。原来只需一根网线，就可以来一次 VLAN 风暴！</p><p><strong>为什么一根网线插交换机的两个口会产生风暴？因为交换机会把发往广播地址的包，转发到所有接口上。如果有两个接口回路了，一旦出现广播包，就会彼此不断循环发送，耗尽整个设备的带宽。别小看交换机，它天生就是为发包设计的，风暴能把每个接口都占满，打出背板带宽的流量。STP 协议就是为了解决这个问题，进行回路检测。</strong></p><p>尽管了解了这个新技能，但物理层的知识基本派不上用场，也就淡忘了 —— 直到发现寝室座位下有信号的那天。</p><p>根据回忆，寝室之间还打过局域网游戏，显然这不是独立的网段，于是更加信心满满了！</p><p>立即找来一根网线，减掉一边的水晶头，刮掉外皮，然后把对应的四股两两粘上。果然，附近的寝室开始传来 —— 不，先是寂静了几秒，接着陆续传来的尖叫声，吼声。“卡了！”，“谁掉了？“，什么情况？”，“靠，断网了~~~”。。。</p><p>跑出走廊一看，整栋楼都暴动了！原来这寝室楼根本就没划 VLAN，所有几百号寝室都是连在一起的！！！</p><p>这时既兴奋又担心。兴奋的是，以后有了电脑可以抓上千人的流量了。担心的是，现在只想恶搞自己寝室，不想牵扯所有人。</p><p>不管怎样，行动还是继续。熄灯后本该休息，断了所有的也没什么不好。</p><p>这时技术上已无大碍，就差实施了。如何从容而又隐蔽的操作呢？</p><p>为了不暴露没电脑还插着根网线那么荒唐，于是尽量沿着有遮挡的柜子布线，从衣柜后一直拖到床铺。剩下的水平部分就埋在床边的缝隙里，并用席子盖着。</p><p>整个布局不凑近仔细看，根本发现不了~</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic3.zhimg.com/77d8bc1a1dabbd44ac3ad52eda1d2976_b.png&amp;quot; data-rawwidth=&amp;quot;616&amp;quot; data-rawheight=&amp;quot;400&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;616&amp;quot; data-original=&amp;quot;https://pic3.zhimg.com/77d8bc1a1dabbd44ac3ad52eda1d2976_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic3.zhimg.com/77d8bc1a1dabbd44ac3ad52eda1d2976_b.png" data-rawwidth="616" data-rawheight="400" class="origin_image zh-lightbox-thumb lazy" width="616" data-original="https://pic3.zhimg.com/77d8bc1a1dabbd44ac3ad52eda1d2976_r.png" data-actualsrc="https://pic3.zhimg.com/77d8bc1a1dabbd44ac3ad52eda1d2976_b.png" style="width: 584px; height: 379.221px; display: block;"/><br/><p>当晚熄灯后，夜猫子们又开始蠢蠢欲动了，我也迫不及待的开始试验。和其他几个同学一样，假装在玩手机，实际已开始悄悄的接线，颇有地下情报员的感觉。</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic2.zhimg.com/ef10cb29a6628618bda5f4768dfce855_b.png&amp;quot; data-rawwidth=&amp;quot;537&amp;quot; data-rawheight=&amp;quot;288&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;537&amp;quot; data-original=&amp;quot;https://pic2.zhimg.com/ef10cb29a6628618bda5f4768dfce855_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic2.zhimg.com/ef10cb29a6628618bda5f4768dfce855_b.png" data-rawwidth="537" data-rawheight="288" class="origin_image zh-lightbox-thumb lazy" width="537" data-original="https://pic2.zhimg.com/ef10cb29a6628618bda5f4768dfce855_r.png" data-actualsrc="https://pic2.zhimg.com/ef10cb29a6628618bda5f4768dfce855_b.png" style="width: 537px; height: 288px; display: block;"/><br/><p>当搭上最后一股时，流畅的游戏声立即出现了卡顿。毕竟整栋楼都在这个 LAN 里，广播包的数量是相当多的。</p><p>只听得游戏剩背景音乐，却没有音效了！</p><p>想着 100Mbps 的流量从手中捏着的网线穿过，仿佛看见密密麻麻的 ARP、NetBIOS 广播在黑暗中闪过 —— 还有那少得可怜的、被挤掉的游戏数据包。</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic4.zhimg.com/e452952852e913dbd1e3671d0d4ebb57_b.png&amp;quot; data-rawwidth=&amp;quot;607&amp;quot; data-rawheight=&amp;quot;296&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;607&amp;quot; data-original=&amp;quot;https://pic4.zhimg.com/e452952852e913dbd1e3671d0d4ebb57_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic4.zhimg.com/e452952852e913dbd1e3671d0d4ebb57_b.png" data-rawwidth="607" data-rawheight="296" class="origin_image zh-lightbox-thumb lazy" width="607" data-original="https://pic4.zhimg.com/e452952852e913dbd1e3671d0d4ebb57_r.png" data-actualsrc="https://pic4.zhimg.com/e452952852e913dbd1e3671d0d4ebb57_b.png" style="width: 584px; height: 284.784px; display: block;"/><br/><p>下午的骚动又一次爆发了。尽管熄灯后少得多，但在夜晚的环境里，显然越发清晰。</p><p>被断开的大多不甘心，还想继续玩。这一次，不打算这么暴力了，万一触发了回路检测，说不定整楼就被封了。</p><p>于是，改成搭上几秒，断开。再搭上、断开。。。游戏虽能运行，但不断陷于卡顿之中。没多久，传来一阵阵温馨的关机声，纷纷洗洗睡了。</p><p>首战告捷！终于睡了个好觉。</p><br/><strong>改良 v1</strong><p>刚开始的几天里，效果非常理想，大家都乖乖的提前睡觉了。</p><p>不过没多久他们就发现，网络过会就会恢复的。原因很简单，哥睡着前就把线放开了，于是他们又开始了疯狂。</p><p>在迷迷糊糊睡梦中，要把网线重新搭上会困难的多。经常把不相干的也缠在了一起，结果就没效果了。</p><p>于是，需要一次用户体验上的改进。</p><p>事实上，其中三股线都是事先粘好的，实际就控制一股而已。不如把那三股都提前隐藏起来，只留一股在身旁，这样就不会搭错了。</p><p>换了根网线重新制作。这次，直接把其中 3 股用胶布粘好，藏在衣柜后面，只留一股拖上来。线路也细了不少。</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic3.zhimg.com/40a93d38ccde2b090b5e46b4ae5fffb6_b.png&amp;quot; data-rawwidth=&amp;quot;515&amp;quot; data-rawheight=&amp;quot;343&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;515&amp;quot; data-original=&amp;quot;https://pic3.zhimg.com/40a93d38ccde2b090b5e46b4ae5fffb6_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic3.zhimg.com/40a93d38ccde2b090b5e46b4ae5fffb6_b.png" data-rawwidth="515" data-rawheight="343" class="origin_image zh-lightbox-thumb lazy" width="515" data-original="https://pic3.zhimg.com/40a93d38ccde2b090b5e46b4ae5fffb6_r.png" data-actualsrc="https://pic3.zhimg.com/40a93d38ccde2b090b5e46b4ae5fffb6_b.png" style="width: 515px; height: 343px; display: block;"/><br/><p>这样，就和电路开关一样了。总共就两根线，搭上或分开就行。</p><p>即使在睡梦中，也只需动动手指，就能轻松自如的控制整楼的网络了！</p><br/><strong>改良 v2</strong><p>不过这么简陋的设备，总会有操作失误的时候。</p><p>在一个周末的半夜，被通宵的吵醒后，狠狠的搭上了网线，然后继续睡。没想到这一次太困，直接沉睡了过去。直到早上 10 点多，才被敲门声惊醒。</p><p>原以为是隔壁同学，但敲门不断，打开后发现进来一个背着工具包的大叔。这时，才猛然意识到，搭着的网线忘了断开了！！！整整断了一晚，都查上门来了！</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic1.zhimg.com/ed40e4d1fbf63617292e4d60622bfb00_b.png&amp;quot; data-rawwidth=&amp;quot;647&amp;quot; data-rawheight=&amp;quot;374&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;647&amp;quot; data-original=&amp;quot;https://pic1.zhimg.com/ed40e4d1fbf63617292e4d60622bfb00_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic1.zhimg.com/ed40e4d1fbf63617292e4d60622bfb00_b.png" data-rawwidth="647" data-rawheight="374" class="origin_image zh-lightbox-thumb lazy" width="647" data-original="https://pic1.zhimg.com/ed40e4d1fbf63617292e4d60622bfb00_r.png" data-actualsrc="https://pic1.zhimg.com/ed40e4d1fbf63617292e4d60622bfb00_b.png" style="width: 584px; height: 337.583px; display: block;"/><br/><p>这时也来不及收拾了，心想这回终于要露陷了。不过那师傅一眼扫去，发现我们桌子上都是干干净净的，啥也没有。唯独敞着个笔记本，而且还没关机。于是上前拔掉了网线，然后走了。</p><p>侥幸躲过了这一劫，迫切需要改进了。</p><p>如果能睡前开启，睡着后自动关闭，那就十分理想了。再也不用睡梦中用意念去断开了。</p><p>于是打算做一个有弹性的开关，必须按着才会开启，松开就关闭。这样睡着后身体放松就自动断开了。</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic3.zhimg.com/f4182c19a11ff6b8a4fee41ba69fa2a6_b.png&amp;quot; data-rawwidth=&amp;quot;247&amp;quot; data-rawheight=&amp;quot;213&amp;quot; class=&amp;quot;content_image&amp;quot; width=&amp;quot;247&amp;quot;&amp;amp;gt;</noscript><img src="https://pic3.zhimg.com/f4182c19a11ff6b8a4fee41ba69fa2a6_b.png" data-rawwidth="247" data-rawheight="213" class="content_image lazy" width="247" data-actualsrc="https://pic3.zhimg.com/f4182c19a11ff6b8a4fee41ba69fa2a6_b.png" style="width: 247px; height: 213px; display: block;"/><br/><p>经过一番改进，把开关做得无比隐蔽：把两根线塞到一个袜子里，里面塞了棉布等等有弹性的东西。正常情况线路是分开的，但轻轻往下压就会搭住，放开后又恢复正常。</p><p>不过袜子捏手里也怪怪的，于是就藏到脚后头。至此，每当夜晚吵闹时，只要脚趾头稍稍踮一下，周围的气氛就立即变得格外安静。</p><p>到此，总共花了两块钱打造的装备，能让脚趾来控制上千人的网络状态，简直太有成就感了：）</p><noscript>&amp;amp;lt;img src=&amp;quot;https://pic1.zhimg.com/1b0d6c0e5d3e99232cfa055a3291b348_b.png&amp;quot; data-rawwidth=&amp;quot;570&amp;quot; data-rawheight=&amp;quot;220&amp;quot; class=&amp;quot;origin_image zh-lightbox-thumb&amp;quot; width=&amp;quot;570&amp;quot; data-original=&amp;quot;https://pic1.zhimg.com/1b0d6c0e5d3e99232cfa055a3291b348_r.png&amp;quot;&amp;amp;gt;</noscript><img src="https://pic1.zhimg.com/1b0d6c0e5d3e99232cfa055a3291b348_b.png" data-rawwidth="570" data-rawheight="220" class="origin_image zh-lightbox-thumb lazy" width="570" data-original="https://pic1.zhimg.com/1b0d6c0e5d3e99232cfa055a3291b348_r.png" data-actualsrc="https://pic1.zhimg.com/1b0d6c0e5d3e99232cfa055a3291b348_b.png" style="width: 570px; height: 220px; display: block;"/><br/><p>没多久，大家似乎发现了规律，只要声音太响网就会卡，但无奈又找不到原因。于是都变得乖乖的安静上网了。（每次回想起就特别搞笑）</p><p>当然，这装置只投入使用了半年。第二个学期大家都装了电脑，于是一起愉快的通宵上网了。</p></div><p><br/></p>', '', 0, '2015-12-10');

