CREATE TABLE IF NOT EXISTS `user_fdata` (
  `userId` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `userName` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '用户姓名',
  `userPassword` varchar(50) NOT NULL COMMENT '用户密码',
  `userMail` varchar(50) NOT NULL COMMENT '用户邮箱',
  `userNumber` varchar(15) NOT NULL COMMENT '用户号码',
  PRIMARY KEY (`userId`)
) ENGINE=InnoDB  DEFAULT CHARSET=gbk COMMENT='用户基本数据表' AUTO_INCREMENT=9 ;

